def hackerrankInString(s):

    # Answers if 'hackerrank' is in a string considering amount of characters and its order

    i = 0
    for letter in 'hackerrank':
        if letter in s[i:]:
            i = s.index(letter, i) + 1
        else:
            return 'NO'
    return 'YES'



